from sqlalchemy.orm import Session

from app.core.pagination import paginate_query

from app.models import Account, AccountType
from app.modules.accounts.schemas import AccountCreate, AccountUpdate


def create_account(db: Session, user_id: int, payload: AccountCreate) -> Account:
    account = Account(
        user_id=user_id,
        bank_name=payload.bank_name,
        account_type=AccountType(payload.account_type),
        nickname=payload.nickname,
    )
    db.add(account)
    db.commit()
    db.refresh(account)
    return account


def list_accounts(db: Session, user_id: int, skip: int, limit: int) -> tuple[list[Account], int]:
    query = db.query(Account).filter(Account.user_id == user_id)
    return paginate_query(query, skip=skip, limit=limit)


def get_account(db: Session, user_id: int, account_id: int) -> Account | None:
    return (
        db.query(Account)
        .filter(Account.user_id == user_id, Account.id == account_id)
        .first()
    )


def update_account(
    db: Session,
    user_id: int,
    account_id: int,
    payload: AccountUpdate,
) -> Account | None:
    account = get_account(db, user_id=user_id, account_id=account_id)
    if not account:
        return None

    if payload.bank_name is not None:
        account.bank_name = payload.bank_name
    if payload.account_type is not None:
        account.account_type = AccountType(payload.account_type)
    if payload.nickname is not None:
        account.nickname = payload.nickname

    db.commit()
    db.refresh(account)
    return account


def delete_account(db: Session, user_id: int, account_id: int) -> bool:
    account = get_account(db, user_id=user_id, account_id=account_id)
    if not account:
        return False
    db.delete(account)
    db.commit()
    return True

from datetime import UTC, datetime

from sqlalchemy.orm import Session

from app.core.pagination import paginate_query

from app.models import Account, Transaction
from app.modules.transactions.schemas import TransactionCreate, TransactionUpdate


def create_transaction(db: Session, user_id: int, payload: TransactionCreate) -> Transaction:
    account = (
        db.query(Account)
        .filter(Account.id == payload.account_id, Account.user_id == user_id)
        .first()
    )
    if not account:
        raise ValueError("Account not found")
    transaction = Transaction(
        account_id=payload.account_id,
        amount=payload.amount,
        merchant=payload.merchant,
        description=payload.description,
        transaction_date=payload.transaction_date or datetime.now(UTC),
        transaction_type=payload.transaction_type,
        payment_method=payload.payment_method,
        card_last4=payload.card_last4,
        installments_total=payload.installments_total,
        installments_current=payload.installments_current,
        category_id=payload.category_id,
        raw_email_id=payload.raw_email_id,
    )
    db.add(transaction)
    db.commit()
    db.refresh(transaction)
    return transaction


def list_transactions(
    db: Session,
    user_id: int,
    skip: int,
    limit: int,
) -> tuple[list[Transaction], int]:
    query = (
        db.query(Transaction)
        .join(Account, Transaction.account_id == Account.id)
        .filter(Account.user_id == user_id)
    )
    return paginate_query(query, skip=skip, limit=limit)


def list_transactions_filtered(
    db: Session,
    user_id: int,
    skip: int,
    limit: int,
    account_id: int | None = None,
    start_date: datetime | None = None,
    end_date: datetime | None = None,
    category_id: int | None = None,
) -> tuple[list[Transaction], int]:
    query = (
        db.query(Transaction)
        .join(Account, Transaction.account_id == Account.id)
        .filter(Account.user_id == user_id)
    )

    if account_id is not None:
        query = query.filter(Transaction.account_id == account_id)
    if start_date is not None:
        query = query.filter(Transaction.transaction_date >= start_date)
    if end_date is not None:
        query = query.filter(Transaction.transaction_date <= end_date)
    if category_id is not None:
        query = query.filter(Transaction.category_id == category_id)

    return paginate_query(query, skip=skip, limit=limit)


def get_transaction(db: Session, user_id: int, transaction_id: int) -> Transaction | None:
    return (
        db.query(Transaction)
        .join(Account, Transaction.account_id == Account.id)
        .filter(Account.user_id == user_id, Transaction.id == transaction_id)
        .first()
    )


def update_transaction(
    db: Session,
    user_id: int,
    transaction_id: int,
    payload: TransactionUpdate,
) -> Transaction | None:
    transaction = get_transaction(db, user_id=user_id, transaction_id=transaction_id)
    if not transaction:
        return None

    if payload.account_id is not None:
        account = (
            db.query(Account)
            .filter(Account.id == payload.account_id, Account.user_id == user_id)
            .first()
        )
        if not account:
            raise ValueError("Account not found")
        transaction.account_id = payload.account_id

    if payload.amount is not None:
        transaction.amount = payload.amount
    if payload.merchant is not None:
        transaction.merchant = payload.merchant
    if payload.description is not None:
        transaction.description = payload.description
    if payload.transaction_date is not None:
        transaction.transaction_date = payload.transaction_date
    if payload.transaction_type is not None:
        transaction.transaction_type = payload.transaction_type
    if payload.payment_method is not None:
        transaction.payment_method = payload.payment_method
    if payload.card_last4 is not None:
        transaction.card_last4 = payload.card_last4
    if payload.installments_total is not None:
        transaction.installments_total = payload.installments_total
    if payload.installments_current is not None:
        transaction.installments_current = payload.installments_current
    if payload.category_id is not None:
        transaction.category_id = payload.category_id

    db.commit()
    db.refresh(transaction)
    return transaction


def delete_transaction(db: Session, user_id: int, transaction_id: int) -> bool:
    transaction = get_transaction(db, user_id=user_id, transaction_id=transaction_id)
    if not transaction:
        return False
    db.delete(transaction)
    db.commit()
    return True

from datetime import UTC, datetime, timedelta

from sqlalchemy import func
from sqlalchemy.orm import Session

from app.core.pagination import paginate_query

from app.models import Budget, BudgetPeriod, Category, Transaction
from app.modules.budgets.schemas import BudgetCreate, BudgetSummary


def create_budget(db: Session, user_id: int, payload: BudgetCreate) -> Budget:
    budget = Budget(
        user_id=user_id,
        category_id=payload.category_id,
        amount_limit=payload.amount_limit,
        period=BudgetPeriod(payload.period),
        start_date=payload.start_date or datetime.now(UTC),
    )
    db.add(budget)
    db.commit()
    db.refresh(budget)
    return budget


def list_budgets(db: Session, user_id: int, skip: int, limit: int) -> tuple[list[Budget], int]:
    query = db.query(Budget).filter(Budget.user_id == user_id)
    return paginate_query(query, skip=skip, limit=limit)


def get_budget_summary(
    db: Session,
    user_id: int,
    budget_id: int,
    include_subcategories: bool,
) -> BudgetSummary | None:
    budget = db.query(Budget).filter(Budget.id == budget_id, Budget.user_id == user_id).first()
    if not budget:
        return None

    period_start, period_end = _resolve_period_window(budget.start_date, budget.period)
    category_ids = _collect_category_ids(db, budget.category_id, include_subcategories)

    amount_spent = (
        db.query(func.coalesce(func.sum(Transaction.amount), 0.0))
        .filter(Transaction.category_id.in_(category_ids))
        .filter(Transaction.transaction_date >= period_start)
        .filter(Transaction.transaction_date < period_end)
        .scalar()
    )
    amount_remaining = budget.amount_limit - amount_spent

    return BudgetSummary(
        budget_id=budget.id,
        category_id=budget.category_id,
        amount_limit=budget.amount_limit,
        amount_spent=amount_spent,
        amount_remaining=amount_remaining,
        period_start=period_start,
        period_end=period_end,
        include_subcategories=include_subcategories,
    )


def _resolve_period_window(start_date: datetime, period: BudgetPeriod) -> tuple[datetime, datetime]:
    now = datetime.now(UTC)
    if period == BudgetPeriod.weekly:
        return _rolling_window(start_date, now, days=7)
    if period == BudgetPeriod.monthly:
        return _rolling_window_months(start_date, now, months=1)
    return _rolling_window_years(start_date, now, years=1)


def _rolling_window(start_date: datetime, now: datetime, days: int) -> tuple[datetime, datetime]:
    delta_days = (now - start_date).days
    if delta_days < 0:
        return start_date, start_date + timedelta(days=days)
    periods = delta_days // days
    window_start = start_date + timedelta(days=periods * days)
    window_end = window_start + timedelta(days=days)
    return window_start, window_end


def _rolling_window_months(start_date: datetime, now: datetime, months: int) -> tuple[datetime, datetime]:
    window_start = start_date
    window_end = _add_months(window_start, months)
    while window_end <= now:
        window_start = window_end
        window_end = _add_months(window_start, months)
    return window_start, window_end


def _rolling_window_years(start_date: datetime, now: datetime, years: int) -> tuple[datetime, datetime]:
    window_start = start_date
    window_end = _add_years(window_start, years)
    while window_end <= now:
        window_start = window_end
        window_end = _add_years(window_start, years)
    return window_start, window_end


def _add_months(value: datetime, months: int) -> datetime:
    month = value.month - 1 + months
    year = value.year + month // 12
    month = month % 12 + 1
    day = min(value.day, _days_in_month(year, month))
    return value.replace(year=year, month=month, day=day)


def _add_years(value: datetime, years: int) -> datetime:
    year = value.year + years
    day = min(value.day, _days_in_month(year, value.month))
    return value.replace(year=year, day=day)


def _days_in_month(year: int, month: int) -> int:
    if month == 12:
        next_month = datetime(year + 1, 1, 1)
    else:
        next_month = datetime(year, month + 1, 1)
    return (next_month - timedelta(days=1)).day


def _collect_category_ids(db: Session, category_id: int, include_subcategories: bool) -> list[int]:
    if not include_subcategories:
        return [category_id]
    ids = [category_id]
    queue = [category_id]
    while queue:
        current = queue.pop(0)
        children = db.query(Category.id).filter(Category.parent_id == current).all()
        for (child_id,) in children:
            if child_id not in ids:
                ids.append(child_id)
                queue.append(child_id)
    return ids

from fastapi import APIRouter, Depends, HTTPException
from sqlalchemy.orm import Session

from app.core.database import get_db
from app.core.pagination import PaginationParams, get_pagination_params
from app.models import User
from app.modules.accounts.schemas import AccountCreate, AccountListResponse, AccountRead, AccountUpdate
from app.modules.accounts.service import (
    create_account,
    delete_account,
    get_account,
    list_accounts,
    update_account,
)
from app.modules.auth.router import get_current_user

router = APIRouter(prefix="/accounts", tags=["accounts"])


@router.post("/", response_model=AccountRead)
def create(
    payload: AccountCreate,
    db: Session = Depends(get_db),
    current_user: User = Depends(get_current_user),
):
    return create_account(db, user_id=current_user.id, payload=payload)


@router.get("/", response_model=AccountListResponse)
def list_all(
    db: Session = Depends(get_db),
    current_user: User = Depends(get_current_user),
    pagination: PaginationParams = Depends(get_pagination_params),
):
    items, total = list_accounts(
        db,
        user_id=current_user.id,
        skip=pagination.skip,
        limit=pagination.limit,
    )
    return {
        "items": items,
        "total": total,
        "skip": pagination.skip,
        "limit": pagination.limit,
    }


@router.get("/{account_id}", response_model=AccountRead)
def get_by_id(
    account_id: int,
    db: Session = Depends(get_db),
    current_user: User = Depends(get_current_user),
):
    account = get_account(db, user_id=current_user.id, account_id=account_id)
    if not account:
        raise HTTPException(status_code=404, detail="Account not found")
    return account


@router.put("/{account_id}", response_model=AccountRead)
def update(
    account_id: int,
    payload: AccountUpdate,
    db: Session = Depends(get_db),
    current_user: User = Depends(get_current_user),
):
    account = update_account(db, user_id=current_user.id, account_id=account_id, payload=payload)
    if not account:
        raise HTTPException(status_code=404, detail="Account not found")
    return account


@router.delete("/{account_id}")
def delete(
    account_id: int,
    db: Session = Depends(get_db),
    current_user: User = Depends(get_current_user),
):
    removed = delete_account(db, user_id=current_user.id, account_id=account_id)
    if not removed:
        raise HTTPException(status_code=404, detail="Account not found")
    return {"status": "deleted"}

